<?phpclass Article{  public $ID;  public $Date;  public $Name;  public $Title;  public $Author;  public $AuthorName; // Aggregate  public $AuthorSignature; // Aggregate  public $Text;  public $Comments; // Array of UserID, Comment text    // Access rights  // 0 - Guests, Allies, Corp Members, Managers, Directors  // 1 - Allies, Corp Members, Managers, Directors  // 2 - Corp Members, Managers, Directors  // 3 - Managers, Directors  // 4 - Directors  public $ReadAccess;  public $WriteAccess;    // Hidden articles do not show up in the public article listing.  // If an article is hidden only the author and directors will see it.  public $IsHidden;    function __construct()   {  }}?>