<?phpclass Cron{  public $ID;  public $Title;  public $Developer;  public $DeveloperName; // Aggregate  public $ScheduleType;  public $Source;  public $LastRun;  public $FullPath; // Aggregate  public $FileExists; // Aggregate    // Schedule types  const Hourly_00 = 0;  const Hourly_30 = 1;  const Daily_00 = 2;  const Daily_11 = 3;  const Daily_12 = 4;  const Weekly_Monday = 5;  const Weekly_Wednesday = 6;  const Weekly_Friday = 7;  const Weekly_Saturday = 8;  const Weekly_Sunday = 9;    function __construct()   {  }  // *******************************************************  // Returns a string containing the schedule type  // *******************************************************    public function ScheduleName()  {    $crontypes = array("Hourly at xx:00", "Hourly at xx:30", "Daily at 00:00 GMT", "Daily at 11:00 GMT", "Daily at 12:00 GMT", "Weekly on Mondays at 00:00 GMT", "Weekly on Wednesdays at 00:00 GMT", "Weekly on Fridays at 00:00 GMT", "Weekly on Saturdays at 00:00 GMT", "Weekly on Sundays at 00:00 GMT");    return $crontypes[$this->ScheduleType];  }}?>