<?phpclass Feed{  public $Title;  public $Link;  public $Description;  public $Summary;  const SUMMARY_LENGTH = 250;    function __construct($title, $link, $description)   {    $this->Title = $title;    $this->Link = $link;    $this->Description = $description;    $description = strip_tags($description);    $this->Summary = substr($description, 0,Feed::SUMMARY_LENGTH);    if(strlen($description) > Feed::SUMMARY_LENGTH) $this->Summary .= "...";  }    // *******************************************************  // Reads a news feed  // *******************************************************    public static function Read($url)  {    $result = array();    $raw = @file_get_contents($url);		    if($raw == FALSE) return array();    if(substr($raw, 0, 5) != "<?xml") return array();    try    {          $xml = new SimpleXMLElement($raw);    }    catch(Exception $e)    {      return array();    }    $items = $xml->item;    foreach($items as $item)    {      $result[] = new Feed($item->title, $item->link, $item->description);    }		    return $result;  }  }?>